import React, { Component } from 'react'




class TeacherHome extends Component {
    render() {

        let name = this.props.teacherName.name
        console.log(this.props.teacherName)




        return (
            <React.Fragment>
                <h1>Welcome {name}!</h1>
                Students
                <select
                    defaultValue=""
                    name="studentList"
                    id="studentList"
                    onChange={this.handleFieldChange}

                >
                    <option value="">Look for a student</option>
                    {this.props.students.map(e => (
                        <option key={e.id} id="students" value={e.id}>
                            {e.name}
                        </option>
                    ))}
                </select>
                <br></br>
                Parents
                <select
                    defaultValue=""
                    name="studentList"
                    id="studentList"
                    onChange={this.handleFieldChange}

                >
                    <option value="">Look for a Parent</option>
                    {this.props.parents.map(e => (
                        <option key={e.id} id="students" value={e.id}>
                            {e.name}
                        </option>
                    ))}
                </select>
                <button type="button"
                    onClick={() => this.props.history.push("/animals/new")}
                    className="btn btn-success">
                    Add New Animal
                    </button>

            </React.Fragment>
        )
    }
}

export default TeacherHome
