import React, { Component } from "react";

let makeid = () => {
    var text = "";
    var possible = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

    for (var i = 0; i < 5; i++)
        text += possible.charAt(Math.floor(Math.random() * possible.length));

    return text;
}


export default class EventForm extends Component {
    // Set initial state
    state = {
        "userTypeId": 1,
        "teacherId": Number(sessionStorage.getItem("credentials")),
        "password": makeid(),
        "accountId": makeid(),
        "parentId": 0
    };


    handleFieldChange = evt => {
        const stateToChange = {};
        stateToChange[evt.target.id] = evt.target.value;
        this.setState(stateToChange);
    };

    NewStudent = evt => {
        evt.preventDefault();
        if (this.state.eventName === "") {
            window.alert("Please Enter Event Name");
        }
        else {
            const student = {
                name: this.state.name,
                accountId: this.state.accountId,
                phoneNumber: this.phoneNumber,
                emailAddress: this.emailAddress,
                password: this.password,
                teacherId: this.teacherId,
                lessonDayId: this.lessonDayId,
                parentId: this.parentId,
                lessonTime: this.lessonTime,
                instrumentId: this.instrumentId,
                locationId: this.locationId,
                lengthId: this.lengthId,
                userTypeId: this.state.userTypeId,

            };
            this.props.addStudent(student)
                .then(() => this.props.history.push("/TeacherHome"));
        }
    };

    render() {
        return (
            <React.Fragment>
                {console.log(this.state)}
                <form className="animalForm">
                    <div className="form-group">
                        <label htmlFor="eventName">Student Name</label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            onChange={this.handleFieldChange}
                            id="name"
                            placeholder="First and last"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="phoneNumber">Phone Number</label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            onChange={this.handleFieldChange}
                            id="phoneNumber"
                            placeholder="xxx-xxx-xxxx"
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="emailAddress">Email Address</label>
                        <input
                            type="text"
                            required
                            className="form-control"
                            onChange={this.handleFieldChange}
                            id="emailAddress"
                            placeholder=""
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="instrument">Instrument</label>
                        <br></br>
                        <select
                            defaultValue=""
                            name="studentList"
                            id="instrumentId"
                            onChange={this.handleFieldChange}

                        >
                            <option value=""></option>
                            {this.props.instruments.map(e => (
                                <option key={e.id} id="instruments" value={e.id}>
                                    {e.name}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="location">Lesson Location</label>
                        <br></br>
                        <select
                            defaultValue=""
                            name="locationList"
                            id="locationId"
                            onChange={this.handleFieldChange}

                        >
                            <option value=""></option>
                            {this.props.locations.map(e => (
                                <option key={e.id} id="locations" value={e.id}>
                                    {e.location}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="lengths">Lesson Lengths</label>
                        <br></br>
                        <select
                            defaultValue=""
                            name="length"
                            id="lengthId"
                            onChange={this.handleFieldChange}

                        >
                            <option value=""></option>
                            {this.props.lengths.map(e => (
                                <option key={e.id} id="lengths" value={e.id}>
                                    {e.length}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="days">Lesson Days</label>
                        <br></br>
                        <select
                            defaultValue=""
                            name="lessonDay"
                            id="lessonDayId"
                            onChange={this.handleFieldChange}

                        >
                            <option value=""></option>
                            {this.props.lessonDays.map(e => (
                                <option key={Number(e.id)} id="days" value={Number(e.id)}>
                                    {e.day}
                                </option>
                            ))}
                        </select>
                    </div>

                    <button
                        type="submit"
                        onClick={this.NewStudent}
                        className="btn btn-primary"
                    >
                        Submit
          </button>
                </form>
            </React.Fragment>
        );
    }
}
